CREATE TABLE tabelao ( 
    tab_cop_edicao VARCHAR2(100), 
    tab_par_data VARCHAR2(100), 
    tab_par_fase VARCHAR2(100), 
    tab_est_nome VARCHAR2(100), 
    tab_cid_nome VARCHAR2(100), 
    tab_nome_time_a VARCHAR2(100), 
    tab_nome_time_b VARCHAR2(100), 
    tab_par_condicoes_de_vitoria VARCHAR2(100), 
    tab_par_publico VARCHAR2(100), 
    tab_par_id VARCHAR2(100), 
    tab_sel_iniciais_time_a VARCHAR2(100), 
    tab_sel_iniciais_time_b VARCHAR2(100) 
)


update tabelao set tab_cop_edicao = (select cop_id from copas where cop_edicao = tabelao.tab_cop_edicao);
update tabelao set tab_cid_nome = (select cid_id from cidades where cid_nome = tabelao.tab_cid_nome);